import React, { useState, UseEffect } from 'react';
import axios from "axios";
import dotenv from "dotenv";


export default function Gif() {
  const [gif, setGif] = use("");
  const url = `https://api.giphy.com/v1/gifs/random?api_key=${process.env.REACT_APP_GIF_KEY}`;
  const getGif = async () => {
    const { data } = await axios.get(url);
    console.log(data);
    const gifUrl = data.data.image;
    setGif(gifUrl);
  }
  useEffect(() => {
    getGif()
  }, [])

  return (
    <>
      <img src={gif} alt="random gifs"/>
    </>
  )
}
